pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IOAppCore } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/interfaces/IOAppCore.sol";
import { IOAppComposer } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/interfaces/IOAppComposer.sol";
import { OFTComposeMsgCodec } from "@layerzerolabs/lz-evm-oapp-v2/contracts/oft/libs/OFTComposeMsgCodec.sol";

contract LZV2OFTComposer is IOAppComposer {

    address public immutable endpoint;
    address public immutable oApp; // deployed LZV2OFT address

    /// @notice Emitted when a message received.
    /// @param sourceEid The endpoint id of source chain.
    /// @param message The amount of tokens swapped.
    event MessageReceived(uint32 sourceEid, string message);

    /// @notice Constructs the SwapMock contract.
    /// @dev Initializes the contract.
    /// @param _endpoint LayerZero Endpoint address
    /// @param _oApp The address of the OApp that is sending the composed message.
    constructor(address _endpoint, address _oApp) {
        endpoint = _endpoint;
        oApp = _oApp;
    }

    /// @notice Handles incoming composed messages from LayerZero.
    /// @dev Decodes the message payload to perform a token swap.
    ///      This method expects the encoded compose message to contain the swap amount and recipient address.
    /// @param _oApp The address of the originating OApp.
    /// @param /*_guid*/ The globally unique identifier of the message (unused in this mock).
    /// @param _message The encoded message content in the format of the OFTComposeMsgCodec.
    /// @param /*Executor*/ Executor address (unused in this mock).
    /// @param /*Executor Data*/ Additional data for checking for a specific executor (unused in this mock).
    function lzCompose(
        address _oApp,
        bytes32 /*_guid*/,
        bytes calldata _message,
        address /*Executor*/,
        bytes calldata /*Executor Data*/
    ) external payable override {
        require(_oApp == oApp, "!oApp");
        require(msg.sender == endpoint, "!endpoint");
        
        uint32 _srcEid = OFTComposeMsgCodec.srcEid(_message);

        // Extract the composed message from the delivered message using the MsgCodec
        (string memory message) = abi.decode(OFTComposeMsgCodec.composeMsg(_message), (string));

        // Emit an event to log the token swap details
        emit MessageReceived(_srcEid, message);
    }
}","keccak256":"0x61f2c8e8e8f7ba08d56f19a2e4921b6244dbdc3865374396e47044aca63bc626","license":"MIT"}},"version":1}
